import React, { useState } from 'react';

// 내부에서 버튼과 텍스트 필드 컴포넌트 정의
/*const Button = ({ variant = "solid", size = "sm", className = "", children }) => {
  const baseClasses = "px-4 py-2 rounded font-medium";
  const variantClasses = variant === "solid" 
    ? "bg-blue-500 text-white" 
    : "bg-transparent border border-blue-500 text-blue-500";
  const sizeClasses = size === "sm" ? "text-sm" : "text-base";

  return (
    <button className={`${baseClasses} ${variantClasses} ${sizeClasses} ${className}`}>
      {children}
    </button>
  );
};

const Label = ({ htmlFor, children }) => (
  <label htmlFor={htmlFor} className="block text-gray-700 font-medium mb-2">
    {children}
  </label>
);

const Textarea = ({ id, rows, defaultValue }) => (
  <textarea id={id} rows={rows} defaultValue={defaultValue} className="w-full border rounded-md p-2 bg-white" />
);

const Command = ({ children }) => (
  <div className="border rounded-lg shadow-md bg-white">
    {children}
  </div>
);

const CommandInput = ({ placeholder, onChange, onFocus }) => (
  <input 
    type="text" 
    placeholder={placeholder} 
    onChange={onChange} 
    onFocus={onFocus} 
    className="w-full border-b p-2 bg-white" 
  />
);

const CommandList = ({ children }) => (
  <div className="p-2 bg-white max-h-60 overflow-y-auto">
    {children}
  </div>
);

const CommandEmpty = ({ children }) => (
  <div className="text-gray-500 text-center py-2 bg-white">
    {children}
  </div>
);

const CommandGroup = ({ children }) => (
  <div>
    {children}
  </div>
);

const CommandItem = ({ children }) => (
  <div className="flex items-center justify-between p-2 border-b last:border-b-0 bg-white">
    {children}
  </div>
);*/

export const Edit = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [isFocused, setIsFocused] = useState(false);

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleInputFocus = () => {
    setIsFocused(true);
  };

  const handleInputBlur = () => {
    setIsFocused(false);
  };

  // 더미 데이터
  const words = [
    { word: "단어 1", suggestion: "대체 단어 1" },
    { word: "단어 2", suggestion: "대체 단어 2" },
    { word: "단어 3", suggestion: "대체 단어 3" },
    { word: "단어 4", suggestion: "대체 단어 4" },
    { word: "단어 5", suggestion: "대체 단어 5" },
    { word: "단어 6", suggestion: "대체 단어 6" },
    { word: "단어 7", suggestion: "대체 단어 7" },
    { word: "단어 8", suggestion: "대체 단어 8" },
    { word: "단어 9", suggestion: "대체 단어 9" },
    { word: "단어 10", suggestion: "대체 단어 10" },
  ];

  const filteredWords = words.filter(
    (item) => item.word.includes(searchTerm) || item.suggestion.includes(searchTerm)
  );

  return (
    <div className="w-full bg-white">
      <header className="bg-gray-900 text-white py-4 px-6 flex justify-between items-center">
        <div className="flex items-center">
          {/* YouTube 아이콘을 SVG로 대체 */}
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="w-8 h-8 mr-2 text-red-500"
            fill="currentColor"
            viewBox="0 0 24 24"
          >
            <path d="M23.498 6.186a2.92 2.92 0 0 0-2.055-2.057C19.747 3.5 12 3.5 12 3.5s-7.747 0-9.443.629a2.92 2.92 0 0 0-2.055 2.057c-.586 3.303-.586 6.814-.586 6.814s0 3.511.586 6.814a2.92 2.92 0 0 0 2.055 2.057c1.696.629 9.443.629 9.443.629s7.747 0 9.443-.629a2.92 2.92 0 0 0 2.055-2.057c.586-3.303.586-6.814.586-6.814s0-3.511-.586-6.814ZM9.75 15.417V8.583L15.75 12 9.75 15.417Z" />
          </svg>
          <div className="flex items-center space-x-4">
            <h1 className="text-xl font-bold">YouTube SubHelper</h1>
            <button className="text-white hover:text-gray-300">
              마이페이지
            </button>
            <button className="text-white hover:text-gray-300">
              자막편집
            </button>
            <button className="text-white hover:text-gray-300">
              부가기능
            </button>
          </div>
        </div>
        <div className="flex items-center space-x-4">
          <button className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">
            로그인
          </button>
          <button className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">
            회원가입
          </button>
        </div>
      </header>
      <h2 className="bg-white text-gray-900 py-4 px-6 text-xl font-bold flex justify-between items-center">
        자막 편집
        <button className="border border-gray-400 text-gray-900 px-4 py-2 rounded hover:bg-gray-200">
          + 자막 업로드
        </button>
      </h2>
      <div className="flex h-[80vh] bg-white">
        <div className="w-1/2 p-4">
        <div className="mt-6" />
          <div className="bg-white rounded-lg shadow-md p-4 border border-gray-300">
            <div className="flex items-center justify-between mb-2">
              <h2 className="text-xl font-bold text-black">자막 편집</h2>
              <Button variant="outline" size="sm">점검</Button>
            </div>
            <div className="grid gap-4">
              <div>
                <Label htmlFor="content">영상 자막</Label>
                <Textarea id="content" rows={5} defaultValue="요약된 내용 1" />
              </div>
              <div className="relative">
                <Command>
                  <CommandInput 
                    placeholder="단어를 수정하세요..." 
                    onChange={handleInputChange} 
                    onFocus={handleInputFocus} 
                    onBlur={handleInputBlur}
                  />
                  {isFocused && (
                    <CommandList>
                      {filteredWords.length === 0 ? (
                        <CommandEmpty>No results found.</CommandEmpty>
                      ) : (
                        <CommandGroup>
                          {filteredWords.map((item, index) => (
                            <CommandItem key={index}>
                              <Button variant="solid" size="sm" className="mr-2">수정</Button>
                              <span>{item.word}</span>
                              <span className="ml-2 text-gray-500">(추천: {item.suggestion})</span>
                            </CommandItem>
                          ))}
                        </CommandGroup>
                      )}
                    </CommandList>
                  )}
                </Command>
              </div>
              <div className="flex justify-end space-x-2">
                <Button variant="outline" size="sm">취소</Button>
                <Button variant="solid" size="sm">저장</Button>
                <Button variant="solid" size="sm">업로드</Button>
              </div>
            </div>
          </div>
        </div>
        <div className="w-1/2 p-4">
          <div className="bg-white rounded-lg shadow-md p-4">
            <div className="relative w-full h-0 pb-[56.25%]">
              <iframe
                src="https://www.youtube.com/embed/ATK7gAaZTOM?"
                className="absolute top-0 left-0 w-full h-full"
                frameBorder="0"
                allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Edit;
