import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button } from '../components/Components';
import axios from 'axios';

const baseAddress = "http://localhost:3000";

const User = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState({
    name: '',
    user_id: '',
    email: '',
    phone: '',
    password: ''
  });

  useEffect(() => {
    const fetchUserData = async () => {
      const token = localStorage.getItem('token');

      try {
        const response = await axios.get(`${baseAddress}/user/value`, {
          headers: {
            'Authorization': `Bearer ${token}`,
          }
        });

        // 배열의 첫 번째 요소에서 유저 정보 가져오기
        setUserData({
          name: response.data[0].name,
          user_id: response.data[0].user_id,
          email: response.data[0].email,
          phone: response.data[0].phone,
          password: '' // 비밀번호는 빈 문자열로 초기화
        });
      } catch (error) {
        console.error('유저 데이터를 가져오는 중 에러 발생:', error);
      }
    };

    fetchUserData();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData({
      ...userData,
      [name]: value,
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const token = localStorage.getItem('token');

    try {
      const response = await axios.put(`${baseAddress}/user/update`, userData, {
        headers: {
          'Authorization': `Bearer ${token}`,
        }
      });

      alert('회원정보가 성공적으로 수정되었습니다!');
      navigate('/mypage'); // 수정 후 마이페이지로 이동
    } catch (error) {
      const errorMessage = error.response?.data?.message || '회원정보 수정에 실패했습니다.';
      console.error('에러 발생:', errorMessage);
      alert(errorMessage);
    }
  };

  return (
    <div className="w-full max-w-md mx-auto mt-10">
      <h2 className="text-2xl font-bold mb-6">회원정보 수정</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block text-gray-700" htmlFor="name">이름</label>
          <input
            type="text"
            id="name"
            name="name"
            value={userData.name}
            onChange={handleChange}
            className="border rounded w-full py-2 px-3"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700" htmlFor="user_id">아이디</label>
          <input
            type="text"
            id="user_id"
            name="user_id"
            value={userData.user_id}
            onChange={handleChange}
            className="border rounded w-full py-2 px-3"
            readOnly // 아이디는 수정할 수 없도록 설정
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700" htmlFor="email">이메일</label>
          <input
            type="email"
            id="email"
            name="email"
            value={userData.email}
            onChange={handleChange}
            className="border rounded w-full py-2 px-3"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700" htmlFor="phone">전화번호</label>
          <input
            type="text"
            id="phone"
            name="phone"
            value={userData.phone}
            onChange={handleChange}
            className="border rounded w-full py-2 px-3"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700" htmlFor="password">비밀번호</label>
          <input
            type="password"
            id="password"
            name="password"
            value={userData.password}
            onChange={handleChange}
            className="border rounded w-full py-2 px-3"
            required
          />
        </div>
        <Button type="submit" className="bg-blue-500 text-white py-2 px-4 rounded">
          수정하기
        </Button>
      </form>
    </div>
  );
};

export default User;
