import React, { useState } from 'react';
import { Button, Textarea } from '../components/Components';

export const Tools = () => {
  const [subtitles, setSubtitles] = useState(''); // 불러온 자막 상태
  const [showModal, setShowModal] = useState(false); // 모달 상태

  // 더미 자막 데이터
  const dummySubtitles = [
    '자막 1: 안녕하세요',
    '자막 2: 반갑습니다',
    '자막 3: 오늘은 좋은 날이에요',
    '자막 4: React를 배워봅시다',
    '자막 5: 스크롤 가능한 모달입니다',
    '자막 6: 자막 예시 텍스트입니다',
    '자막 7: OpenAI를 이용해봅시다',
    '자막 8: 이 자막은 스크롤 가능합니다',
    '자막 9: 더미 데이터입니다',
    '자막 10: 마지막 자막입니다',
  ];

  // 모달 열기/닫기 토글 함수
  const toggleModal = () => {
    setShowModal(!showModal);
  };

  return (
    <div className="bg-white text-gray-900 min-h-screen">
      <header className="bg-white text-gray-900 py-4 px-6 text-xl font-bold flex justify-between items-center">
        부가 기능
        <Button variant="outline" className="hover:bg-gray-200" onClick={toggleModal}>
          + 자막 불러오기
        </Button>
      </header>

      {/* 모달 */}
      {showModal && (
        <div className="fixed inset-0 flex items-center justify-center z-50 bg-gray-500 bg-opacity-50">
          <div className="bg-white w-1/2 h-1/2 rounded-lg shadow-lg p-4 overflow-y-auto">
            <h2 className="text-xl font-bold mb-4">자막 목록</h2>
            <div className="space-y-2">
              {/* 스크롤 가능한 자막 목록 */}
              {dummySubtitles.map((subtitle, index) => (
                <div key={index} className="p-2 bg-gray-100 rounded">
                  {subtitle}
                </div>
              ))}
            </div>
            <div className="flex justify-end mt-4">
              <Button variant="outline" className="hover:bg-gray-200" onClick={toggleModal}>
                닫기
              </Button>
            </div>
          </div>
        </div>
      )}

      {/* 자막 입력란 */}
      <div className="p-4">
        <h2 className="text-xl font-bold mb-4">불러온 자막</h2>  
        <Textarea
          id="subtitle-input"
          rows={5}
          value={subtitles}
          onChange={(e) => setSubtitles(e.target.value)}
          placeholder="여기에 자막을 입력하세요..."
        />
      </div>
    </div>
  );
};

export default Tools;
