import React, { useState } from 'react';
import { Button, Label, Textarea, Command, CommandInput, CommandList, CommandEmpty, CommandGroup, CommandItem, Input, Select, Audio } from '../components/Components';
import axios from 'axios';  // axios 추가

const baseAddress = "http://localhost:3000";  // 서버 주소

export const Edit = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [isFocused, setIsFocused] = useState(false);
  const [showModal, setShowModal] = useState(false); // 모달 상태 추가
  const [fileName, setFileName] = useState(""); // 파일 이름 저장
  const [name, setProjectName] = useState(""); // 영상 제목 저장
  const [url, setProjectUrl] = useState("");   // 영상 링크 저장

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleInputFocus = () => {
    setIsFocused(true);
  };

  const handleInputBlur = () => {
    setIsFocused(false);
  };

  const handleAddSubtitle = async () => {
    const token = localStorage.getItem('token'); 

    const newSub = {
      project_name: name,
      project_url: url,
      date: new Date().toLocaleDateString('ko-KR', { year: 'numeric', month: 'long', day: 'numeric' }),
    };

    // 서버에 자막 데이터 전송
    try {
      const response = await axios.post(`${baseAddress}/project`, {
          project_name: name,
          project_url: url,
      }, {
          headers: {
              'Authorization': `Bearer ${token}`, // Authorization 헤더에 토큰 포함
          }
      });

      console.log('서버 응답:', response.data);

      setShowModal(false); // 모달 닫기
      setProjectName('');  // 상태 초기화
      setProjectUrl('');   // 상태 초기화
      alert(`자막이 성공적으로 추가되었습니다: ${name} - ${url}`);
    } catch (error) {
      const errorMessage = error.response?.data?.message || '서버 오류가 발생했습니다.'; 
      console.error('에러 발생:', errorMessage);
      alert(errorMessage);
    }
  };

  return (
    <div className="w-full bg-white">
      {/* 상단 페이지: 자막 편집 */}
      <div className="bg-white">
        <header className="bg-white text-gray-900 py-4 px-6 text-xl font-bold flex justify-between items-center">
          자막 편집
          <Button
            variant="outline"
            className="hover:bg-gray-200"
            onClick={() => {
              setProjectName(""); // 모달을 열 때마다 입력된 내용을 초기화
              setProjectUrl("");  // 모달을 열 때마다 입력된 내용을 초기화
              setShowModal(true);
            }}
          >
            + 자막 추가
          </Button>
        </header>
        {/* 자막 추가 모달 */}
        {showModal && (
          <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
            <div className="bg-white p-6 rounded shadow-lg">
              <h2 className="text-xl font-bold text-black mb-4">영상 선택</h2>
              <input 
                type="text" 
                placeholder="영상 제목을 입력하세요.." 
                value={name}
                onChange={(e) => setProjectName(e.target.value)} 
                className="border bg-white p-2 w-full mb-4"
              />
              <input 
                type="text" 
                placeholder="영상 링크를 입력하세요.." 
                value={url} 
                onChange={(e) => setProjectUrl(e.target.value)} 
                className="border bg-white p-2 w-full mb-4"
              />
              <div className="flex justify-end space-x-2">
                <Button 
                  variant="outline" 
                  size="sm" 
                  className="hover:bg-gray-200" 
                  onClick={() => setShowModal(false)}
                >
                  취소
                </Button>
                <Button 
                  variant="solid" 
                  size="sm" 
                  className="hover:bg-blue-600"
                  onClick={handleAddSubtitle}
                >
                  업로드
                </Button>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* 나머지 페이지 내용... */}
    </div>
  );
};

export default Edit;
