import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Button, Label, Textarea, Command, CommandInput, CommandList, CommandEmpty, CommandGroup, CommandItem, Input, Select, Audio } from '../components/Components';

export const Edit = ({ projectId }) => { // projectId를 props로 받습니다.
  const [searchTerm, setSearchTerm] = useState("");
  const [isFocused, setIsFocused] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [fileName, setFileName] = useState("");
  const [subtitleContent, setSubtitleContent] = useState(""); // 자막 내용을 저장하는 상태 추가

  useEffect(() => {
    const fetchSubtitle = async () => {
      try {
        const response = await axios.get(`http://localhost:3000/subtitles/${projectId}`); // 프로젝트 ID에 해당하는 자막 API 호출
        setSubtitleContent(response.data.content); // 응답에서 자막 내용을 가져와 상태에 저장
      } catch (error) {
        console.error('자막을 가져오는 중 오류 발생:', error);
      }
    };

    if (projectId) {
      fetchSubtitle();
    }
  }, [projectId]);

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleInputFocus = () => {
    setIsFocused(true);
  };

  const handleInputBlur = () => {
    setIsFocused(false);
  };

  const filteredWords = words.filter(
    (item) => item.word.includes(searchTerm) || item.suggestion.includes(searchTerm)
  );

  const languageOptions = [
    { value: "", label: "언어 선택", disabled: true },
    { value: "en", label: "영어" },
    { value: "es", label: "스페인어" },
    { value: "fr", label: "프랑스어" },
    { value: "de", label: "독일어" },
    { value: "ja", label: "일본어" },
    { value: "zh", label: "중국어" },
  ];

  const handleDownload = async () => {
    try {
      const response = await axios.get('http://localhost:3000/files/test', {
        responseType: 'blob',
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      document.body.appendChild(link);
      link.click();
      link.parentNode.removeChild(link);
    } catch (error) {
      console.error('파일 다운로드 중 오류 발생:', error);
    }
  };

  return (
    <div className="w-full bg-white">
      <div className="bg-white">
        <header className="bg-white text-gray-900 py-4 px-6 text-xl font-bold flex justify-between items-center">
          자막 편집
          <Button
            variant="outline"
            className="hover:bg-gray-200"
            onClick={() => {
              setFileName("");
              setShowModal(true);
            }}
          >
            + 자막 추가
          </Button>
        </header>
        <div className="flex bg-white">
          <div className="w-1/2 p-4">
            <div className="bg-white rounded-lg shadow-md p-4 border border-gray-300">
              <div className="flex items-center justify-between mb-2">
                <h2 className="text-xl font-bold text-black">자막 수정</h2>
                <div className="flex space-x-2">
                  <Button variant="outline" size="sm">생성</Button>
                  <Button variant="solid" size="sm">점검</Button>
                </div>
              </div>
              <div className="grid gap-4">
                <div>
                  <Label htmlFor="content">영상 자막</Label>
                  <Textarea
                    id="content"
                    rows={5}
                    defaultValue={subtitleContent} // 여기서 자막 내용을 출력
                  />
                </div>
                <div className="relative">
                  <Command>
                    <CommandInput
                      placeholder="단어를 수정하세요..."
                      onChange={handleInputChange}
                      onFocus={handleInputFocus}
                      onBlur={handleInputBlur}
                    />
                    {isFocused && (
                      <CommandList>
                        {filteredWords.length === 0 ? (
                          <CommandEmpty>No results found.</CommandEmpty>
                        ) : (
                          <CommandGroup>
                            {filteredWords.map((item, index) => (
                              <CommandItem key={index}>
                                <Button variant="solid" size="sm" className="mr-2">수정</Button>
                                <span>{item.word}</span>
                                <span className="ml-2 text-gray-500">(추천: {item.suggestion})</span>
                              </CommandItem>
                            ))}
                          </CommandGroup>
                        )}
                      </CommandList>
                    )}
                  </Command>
                </div>
                <div className="mt-7" />
                <div className="flex justify-end space-x-2">
                  <Button variant="outline" size="sm">취소</Button>
                  <Button variant="solid" size="sm">저장</Button>
                  <Button variant="solid" size="sm">업로드</Button>
                  <Button
                    variant="solid"
                    size="sm"
                    onClick={handleDownload}
                  >
                    다운로드
                  </Button>
                </div>
              </div>
            </div>
          </div>
          <div className="w-1/2 p-4">
            <div className="bg-white rounded-lg shadow-md p-4 border border-gray-300">
              <div className="relative w-full h-0 pb-[56.25%]">
                <iframe
                  src="https://www.youtube.com/embed/ATK7gAaZTOM?"
                  className="absolute top-0 left-0 w-full h-full"
                  frameBorder="0"
                  allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                  allowFullScreen
                />
              </div>
            </div>
          </div>
        </div>

        {showModal && (
          <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
            <div className="bg-white p-6 rounded shadow-lg">
              <h2 className="text-xl font-bold text-black mb-4">영상 선택</h2>
              <input 
                type="text" 
                placeholder="영상 제목을 입력하세요.." 
                className="border bg-white p-2 w-full mb-4"
              />
              <input 
                type="text" 
                placeholder="영상 링크를 입력하세요.." 
                value={fileName} 
                onChange={(e) => setFileName(e.target.value)} 
                className="border bg-white p-2 w-full mb-4"
              />
              <div className="flex justify-end space-x-2">
                <Button 
                  variant="outline" 
                  size="sm" 
                  className="hover:bg-gray-200" 
                  onClick={() => setShowModal(false)}
                >
                  취소
                </Button>
                <Button 
                  variant="solid" 
                  size="sm" 
                  className="hover:bg-blue-600"
                  onClick={() => {
                    setShowModal(false);
                  }}
                >
                  업로드
                </Button>
              </div>
            </div>
          </div>
        )}
      </div>

      <div className="bg-white mt-4">
        <header className="bg-white text-gray-900 py-4 px-6 text-xl font-bold flex justify-between items-center">
          번역 및 AI 기능
        </header>
        <div className="flex">
          <div className="w-1/2 p-4">
            <div className="bg-white rounded-lg shadow-md p-4 border border-gray-300">
              <div className="flex items-center justify-between mb-2">
                <h2 className="text-xl font-bold text-black">제목 및 태그 추천</h2>
                <Button variant="outline" className="hover:bg-gray-200">추천받기</Button>
              </div>
              <div className="grid gap-4">
                <div>
                  <Label htmlFor="title">추천 제목</Label>
                  <Input id="title" defaultValue="제목 1" />
                </div>
                <div>
                  <Label htmlFor="tags">추천 태그</Label>
                  <Input id="tags" defaultValue="# 태그 1" />
                  <Input id="tags" defaultValue="# 태그 2" />
                  <Input id="tags" defaultValue="# 태그 3" />
                </div>
                <div className="flex justify-end">
                  <Button variant="solid">저장</Button>
                </div>
              </div>
            </div>
          </div>
          <div className="w-1/2 p-4">
            <div className="bg-white rounded-lg shadow-md p-4 border border-gray-300">
              <div className="flex items-center justify-between mb-2">
                <h2 className="text-xl font-bold text-black">AI 보이스오버</h2>
                <Button variant="outline" className="hover:bg-gray-200">생성</Button>
              </div>
              <div className="grid gap-4">
                <div>
                  <Label htmlFor="voice">음성 선택</Label>
                  <Select id="voice">
                    <option value="">음성 선택</option>
                    <option value="voice1">음성 1</option>
                    <option value="voice2">음성 2</option>
                    <option value="voice3">음성 3</option>
                  </Select>
                </div>
                <div>
                  <Label htmlFor="speed">속도 조절</Label>
                  <Input id="speed" type="range" min="0.5" max="2" step="0.1" defaultValue="1" />
                </div>
                <div className="flex justify-end">
                  <Button variant="solid">저장</Button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
