import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button } from '../components/Components';
import axios from 'axios';

const baseAddress = "http://localhost:3000";

export const Mypage = () => {
  const navigate = useNavigate(); // 페이지 이동을 위한 useNavigate 훅
  const [subtitles, setSubtitles] = useState([]); // 자막 상태 초기화
  const [showModal, setShowModal] = useState(false);
  const [title, setProjectName] = useState(''); // "영상 제목을 입력하세요" 입력값 저장
  const [url, setProjectUrl] = useState('');   // "영상 링크를 입력하세요" 입력값 저장
  const [userData, setUserData] = useState({ name: '', email: '' }); // 유저 데이터 상태

  // useEffect를 사용하여 컴포넌트가 마운트될 때 API 호출
  useEffect(() => {
    const fetchUserData = async () => {
      const token = localStorage.getItem('token'); // 로컬스토리지에서 토큰 가져오기

      try {
        const response = await axios.get(`${baseAddress}/user/value`, {
          headers: {
            'Authorization': `Bearer ${token}`, // 인증 토큰을 헤더에 포함
          }
        });

        // 서버로부터 받은 데이터로 userData 상태 업데이트
        setUserData({
          name: response.data[0].name,
          email: response.data[0].email,
        });
      } catch (error) {
        console.error('유저 데이터를 가져오는 중 에러 발생:', error);
      }
    };

    fetchUserData(); // API 호출 함수 실행
  }, []);

  // 추가된 useEffect로 자막 데이터를 가져오는 코드
  useEffect(() => {
    const fetchSubtitles = async () => {
      const token = localStorage.getItem('token'); // 로컬스토리지에서 토큰 가져오기

      try {
        const response = await axios.get(`${baseAddress}/subtitles`, {
          headers: {
            'Authorization': `Bearer ${token}`, // 인증 토큰을 헤더에 포함
          }
        });
        // 자막 데이터 형식에 맞게 상태 업데이트
        const subtitlesWithTitles = response.data.map(subtitle => ({
          title: subtitle.title,
          date: new Date(subtitle.date).toLocaleDateString('ko-KR', { year: 'numeric', month: 'long', day: 'numeric' }),
        }));
        setSubtitles(subtitlesWithTitles); // 서버에서 받은 자막 데이터로 상태 업데이트
      } catch (error) {
        console.error('자막 데이터를 가져오는 중 에러 발생:', error);
      }
    };

    fetchSubtitles(); // 자막 데이터 가져오는 함수 실행
  }, []);

  // 새로운 자막 데이터를 가져오는 함수
  const fetchProjectTitle = async () => {
    const token = localStorage.getItem('token');
    try {
      const response = await axios.get(`${baseAddress}/project/title`, {
        headers: {
          'Authorization': `Bearer ${token}`,
        }
      });
      return response.data.project_title; // 서버에서 받아온 project_title 반환
    } catch (error) {
      console.error('프로젝트 제목을 가져오는 중 에러 발생:', error);
      return ''; // 에러 발생 시 빈 문자열 반환
    }
  };

  const handleAddSubtitle = async () => {
    const token = localStorage.getItem('token');

    const newSub = {
      title: title, // 입력된 제목 사용
      date: new Date().toLocaleDateString('ko-KR', { year: 'numeric', month: 'long', day: 'numeric' }),
    };

    // 서버에 자막 데이터 전송
    try {
      await axios.post(`${baseAddress}/subtitles`, newSub, {
        headers: {
          'Authorization': `Bearer ${token}`, // 토큰을 헤더에 포함
        }
      });

      // 자막 추가 후 상태 업데이트
      setSubtitles([...subtitles, newSub]);
      setShowModal(false);
      setProjectName(''); // 상태 초기화
      setProjectUrl('');  // 상태 초기화
    } catch (error) {
      const errorMessage = error.response?.data?.message || '서버 오류가 발생했습니다.';
      console.error('에러 발생:', errorMessage);
      alert(errorMessage);
    }
  };

  const handleDeleteSubtitle = (index) => {
    const updatedSubtitles = subtitles.filter((_, i) => i !== index);
    setSubtitles(updatedSubtitles);
  };

  const handleEditClick = () => {
    navigate('/Edit'); // 자막 편집 페이지로 이동
  };

  return (
    <div className="w-full bg-white text-gray-900 min-h-screen">
      <div className="flex flex-col h-screen">
        <header className="bg-white text-gray-900 py-4 px-6 flex justify-between items-center">
          <h2 className="text-xl font-bold">마이페이지</h2>
          <div className="flex space-x-2">
            <Button 
              variant="outline" 
              className="hover:bg-gray-200" 
              onClick={() => setShowModal(true)}
            >
              + 자막 추가
            </Button>
          </div>
        </header>

        {/* 유저 정보 섹션 */}
        <div className="bg-white rounded-lg p-4 border border-gray-300">
          <h2 className="text-xl font-bold">내 정보</h2>
          <p><span className="font-bold">이름: {userData.name}</span></p>
          <p><span className="font-bold">이메일: {userData.email}</span></p>
        </div>

        {/* 자막 리스트 섹션 */}
        <div className="flex-1 overflow-auto p-4">
          <div className="grid gap-4">
            {subtitles.map((subtitle, index) => (
              <div key={index} className="bg-white rounded-lg shadow-md p-4 border border-gray-300">
                <h2 className="text-xl font-bold mb-2">{subtitle.title}</h2>
                <p className="text-sm text-gray-500">{subtitle.date}</p>
                <div className="flex items-center justify-end space-x-2 mt-4">
                  <Button 
                    variant="outline" 
                    size="sm" 
                    onClick={handleEditClick} // 자막 편집 버튼 클릭 시 페이지 이동
                  >
                    자막 편집
                  </Button>
                  <Button 
                    variant="solid" 
                    size="sm" 
                    onClick={() => handleDeleteSubtitle(index)}
                  >
                    삭제
                  </Button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {showModal && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded shadow-lg">
            <h2 className="text-xl font-bold mb-4">영상 선택</h2>
            <input
              type="text"
              placeholder="영상 제목을 입력하세요.."
              value={title}
              onChange={(e) => setProjectName(e.target.value)}
              className="border bg-white p-2 w-full mb-4"
            />
            <input
              type="text"
              placeholder="영상 URL 입력하세요.."
              value={url}
              onChange={(e) => setProjectUrl(e.target.value)}
              className="border bg-white p-2 w-full mb-4"
            />
            <div className="flex justify-end space-x-2">
              <Button
                variant="outline"
                size="sm"
                className="hover:bg-gray-200"
                onClick={() => setShowModal(false)}
              >
                취소
              </Button>
              <Button
                variant="solid"
                size="sm"
                className="hover:bg-blue-600"
                onClick={handleAddSubtitle}
              >
                추가
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Mypage;
