import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button } from '../components/Components';
import axios from 'axios';

const baseAddress = "http://localhost:3000";

export const Mypage = () => {
  const navigate = useNavigate();
  const [subtitles, setSubtitles] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [title, setProjectName] = useState('');
  const [url, setProjectUrl] = useState('');
  const [userData, setUserData] = useState({ name: '', email: '' });

  // 유저 데이터를 가져오는 부분
  useEffect(() => {
    const fetchUserData = async () => {
      const token = localStorage.getItem('token');
      try {
        const response = await axios.get(`${baseAddress}/user/value`, {
          headers: { 'Authorization': `Bearer ${token}` },
        });
        if (response.data && response.data.length > 0) {
          setUserData({
            name: response.data[0].name,
            email: response.data[0].email,
          });
        }
      } catch (error) {
        console.error('유저 데이터를 가져오는 중 에러 발생:', error);
      }
    };

    fetchUserData();
    fetchSubtitles(); // 컴포넌트가 렌더링될 때 자막 목록을 가져옵니다.
  }, []);

  // 자막 목록을 가져오는 함수
  const fetchSubtitles = async () => {
    const token = localStorage.getItem('token');
    try {
      const response = await axios.get(`${baseAddress}/project`, {
        headers: { 'Authorization': `Bearer ${token}` },
      });
      setSubtitles(response.data); // 가져온 자막 목록으로 상태 업데이트
    } catch (error) {
      console.error('자막 목록을 가져오는 중 에러 발생:', error);
    }
  };

  const handleAddSubtitle = async () => {
    const token = localStorage.getItem('token');

    try {
      await axios.post(`${baseAddress}/project`, {
        project_title: title,
        project_url: url,
      }, {
        headers: { 'Authorization': `Bearer ${token}` },
      });

      setProjectName('');
      setProjectUrl('');
      setShowModal(false);

      fetchSubtitles(); // 자막 추가 후 자막 목록을 다시 가져옵니다.
    } catch (error) {
      const errorMessage = error.response?.data?.message || '서버 오류가 발생했습니다.';
      console.error('에러 발생:', errorMessage);
      alert(errorMessage);
    }
  };

  const handleOpenModal = () => {
    setShowModal(true);
  };

  const handleDeleteSubtitle = async (index) => {
    const subtitleToDelete = subtitles[index];

    const token = localStorage.getItem('token');
    try {
      await axios.delete(`${baseAddress}/project/${subtitleToDelete.id}`, {
        headers: { 'Authorization': `Bearer ${token}` },
      });

      fetchSubtitles(); // 삭제 후 자막 목록을 다시 가져옵니다.
    } catch (error) {
      console.error('자막 삭제 중 에러 발생:', error);
    }
  };

  const handleEditClick = () => {
    navigate('/Edit');
  };

  return (
    <div className="w-full bg-white text-gray-900 min-h-screen">
      <div className="flex flex-col h-screen">
        <header className="bg-white text-gray-900 py-4 px-6 flex justify-between items-center">
          <h2 className="text-xl font-bold">마이페이지</h2>
          <div className="flex space-x-2">
            <Button 
              variant="outline" 
              className="hover:bg-gray-200" 
              onClick={handleOpenModal}
            >
              + 자막 추가
            </Button>
          </div>
        </header>

        <div className="bg-white rounded-lg p-4 border border-gray-300">
          <h2 className="text-xl font-bold">내 정보</h2>
          <p><span className="font-bold">이름: {userData.name}</span></p>
          <p><span className="font-bold">이메일: {userData.email}</span></p>
        </div>

        <div className="flex-1 overflow-auto p-4">
          <div className="grid gap-4">
            {subtitles.map((subtitle, index) => (
              <div key={index} className="bg-white rounded-lg shadow-md p-4 border border-gray-300">
                <h2 className="text-xl font-bold mb-2">{subtitle.project_title}</h2>
                <div className="grid gap-2">
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="text-sm text-gray-500">{subtitle.project_url}</p>
                    </div>
                    <div className="flex items-center space-x-2">
                      <Button 
                        variant="outline" 
                        size="sm" 
                        onClick={handleEditClick}
                      >
                        자막 편집
                      </Button>
                      <Button 
                        variant="solid" 
                        size="sm" 
                        onClick={() => handleDeleteSubtitle(index)}
                      >
                        삭제
                      </Button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {showModal && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded shadow-lg">
            <h2 className="text-xl font-bold mb-4">자막 추가</h2>
            <input
              type="text"
              placeholder="자막 제목을 입력하세요.."
              value={title}
              onChange={(e) => setProjectName(e.target.value)}
              className="border bg-white p-2 w-full mb-4"
            />
            <input
              type="text"
              placeholder="영상 URL 입력하세요.."
              value={url}
              onChange={(e) => setProjectUrl(e.target.value)}
              className="border bg-white p-2 w-full mb-4"
            />
            <div className="flex justify-end space-x-2">
              <Button
                variant="outline"
                size="sm"
                className="hover:bg-gray-200"
                onClick={() => setShowModal(false)}
              >
                취소
              </Button>
              <Button
                variant="solid"
                size="sm"
                className="hover:bg-blue-600"
                onClick={handleAddSubtitle}
              >
                추가
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Mypage;
