import React, { useEffect, useState, useRef } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Button, Label, Textarea, Command, CommandInput, CommandList, CommandEmpty, CommandGroup, CommandItem, Input, Select, Audio } from '../components/Components';

export const Edit = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [isFocused, setIsFocused] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [fileName, setFileName] = useState("");
  const [generatedData, setGeneratedData] = useState(""); // 생성된 데이터를 위한 상태 추가


  const { projectId } = useParams();
  const commandRef = useRef(null); // Command 컴포넌트에 대한 참조 생성

  // 외부 클릭 시 드롭다운 닫기
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (commandRef.current && !commandRef.current.contains(event.target)) {
        setIsFocused(false); // 드롭다운 닫기
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [commandRef]);

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleInputFocus = () => {
    setIsFocused(true);
  };

  const handleInputBlur = () => {
    setIsFocused(false);
  };

  // 더미 데이터
  const words = [
    { word: "단어 1", suggestion: "대체 단어 1" },
    { word: "단어 2", suggestion: "대체 단어 2" },
    { word: "단어 3", suggestion: "대체 단어 3" },
    { word: "단어 4", suggestion: "대체 단어 4" },
    { word: "단어 5", suggestion: "대체 단어 5" },
    { word: "단어 6", suggestion: "대체 단어 6" },
    { word: "단어 7", suggestion: "대체 단어 7" },
    { word: "단어 8", suggestion: "대체 단어 8" },
    { word: "단어 9", suggestion: "대체 단어 9" },
    { word: "단어 10", suggestion: "대체 단어 10" },
  ];

  const filteredWords = words.filter(
    (item) => item.word.includes(searchTerm) || item.suggestion.includes(searchTerm)
  );

  const languageOptions = [
    { value: "", label: "언어 선택", disabled: true },
    { value: "en", label: "영어" },
    { value: "es", label: "스페인어" },
    { value: "fr", label: "프랑스어" },
    { value: "de", label: "독일어" },
    { value: "ja", label: "일본어" },
    { value: "zh", label: "중국어" },
  ];

  const handleGenerate = async (event) => {
    event.preventDefault();

    // 기존 formData
    const formData = {
      content_projectID: projectId
    };

    try {
      console.log('프로젝트 ID:', projectId);
      console.log(typeof projectId); // projectId의 타입이 string인지 확인

      // 첫 번째 POST 요청: /work/generateSub
      await axios.post(`http://localhost:3000/work/generateSub`, formData);
      console.log('첫 번째 요청이 성공적으로 전송되었습니다.');

      // 두 번째 POST 요청: /files/readSRT
      const readSRTData = {
        content_projectID: projectId,
        content_language: "kr" // 필요한 언어 추가
      };

      const responseReadSRT = await axios.post(`http://localhost:3000/files/readSRT`, readSRTData);
      console.log('두 번째 요청이 성공적으로 전송되었습니다.');

      // 생성된 데이터 가져오기
      const responseData = responseReadSRT.data; // 서버에서 받은 데이터
      setGeneratedData(responseData); // 상태 업데이트


    } catch (error) {
      console.error('에러 발생:', error.response?.data || error.message);
    }
  };




  const handleCheck = async () => {
    try {
      console.log('점검 요청 전송 중...'); // 요청 전송 전에 로그 추가
      const response = await axios.post('http://localhost:4000/llm', {});
      console.log('점검 결과:', response.data); // 서버로부터 받은 응답 로그
    } catch (error) {
      console.error('점검 요청 중 오류 발생:', error);
    }
  };  

 return (
    <div className="w-full bg-white">
      {/* 상단 페이지: 자막 편집 */}
      <div className="bg-white">
        <header className="bg-white text-gray-900 py-4 px-6 text-xl font-bold flex justify-between items-center">
          자막 편집
          <Button
            variant="outline"
            onClick={() => {
              setFileName(""); // Reset file input
              setShowModal(true);
            }}
          >
            + 자막 추가
          </Button>
        </header>
        <div className="flex bg-white">
          <div className="w-1/2 p-4">
            <div className="bg-white rounded-lg shadow-md p-4 border border-gray-300">
              <div className="flex items-center justify-between mb-2">
                <h2 className="text-xl font-bold text-black">자막 수정</h2>
                <div className="flex space-x-2">
                  <Button variant="outline" size="sm" onClick={handleGenerate}>생성</Button>
                  <Button variant="solid" size="sm" onClick={handleCheck}>점검</Button>
                </div>
              </div>
              <div className="grid gap-4">
                <div>
                  <Label htmlFor="content">영상 자막</Label>
                  <Textarea
                    id="content"
                    rows={5}
                    defaultValue={`0:11 오늘도 아침엔 입에 빵을 물고\n0:15 똑같이 하루를 시작하고...\n1:05 나는 생각은 딱 질색이니까`}
                  />
                </div>
                <div className="relative">
                  <Command>
                    <CommandInput
                      placeholder="단어를 수정하세요..."
                      onChange={handleInputChange}
                      onFocus={handleInputFocus}
                      onBlur={handleInputBlur}
                    />
                    {isFocused && (
                      <CommandList>
                        {filteredWords.length === 0 ? (
                          <CommandEmpty>No results found.</CommandEmpty>
                        ) : (
                          <CommandGroup>
                            {filteredWords.map((item, index) => (
                              <CommandItem key={index}>
                                <Button variant="solid" size="sm" className="mr-2">수정</Button>
                                <span>{item.word}</span>
                                <span className="ml-2 text-gray-500">(추천: {item.suggestion})</span>
                              </CommandItem>
                            ))}
                          </CommandGroup>
                        )}
                      </CommandList>
                    )}
                  </Command>
                </div>
                <div className="flex justify-end space-x-2 mt-7">
                  <Button variant="outline" size="sm">취소</Button>
                  <Button variant="solid" size="sm">저장</Button>
                  <Button variant="solid" size="sm">업로드</Button>
                </div>
              </div>
            </div>
          </div>
          <div className="w-1/2 p-4">
            <div className="bg-white rounded-lg shadow-md p-4 border border-gray-300">
              <div className="relative w-full h-0 pb-[56.25%]">
                <iframe
                  src="https://www.youtube.com/embed/ATK7gAaZTOM?"
                  className="absolute top-0 left-0 w-full h-full"
                  frameBorder="0"
                  allowFullScreen
                />
              </div>
            </div>
          </div>
        </div>

        {/* 자막 추가 모달 */}
        {showModal && (
          <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
            <div className="bg-white p-6 rounded shadow-lg">
              <h2 className="text-xl font-bold text-black mb-4">영상 선택</h2>
              <input
                type="text"
                placeholder="영상 제목을 입력하세요.."
                className="border bg-white p-2 w-full mb-4"
              />
              <input
                type="text"
                placeholder="영상 링크를 입력하세요.."
                value={fileName}
                onChange={(e) => setFileName(e.target.value)}
                className="border bg-white p-2 w-full mb-4"
              />
              <div className="flex justify-end space-x-2">
                <Button variant="outline" size="sm" onClick={() => setShowModal(false)}>취소</Button>
                <Button
                  variant="solid" 
                  size="sm"
                  onClick={() => {
                    // Handle YouTube URL logic here
                    setShowModal(false);
                  }}
                >
                  업로드
                </Button>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* 하단 페이지: 자막 번역 */}
      <div className="bg-white mt-4">
        <header className="bg-white text-gray-900 py-4 px-6 text-xl font-bold flex justify-between items-center">
          번역 및 AI 기능
        </header>
        <div className="flex">
          <div className="w-1/2 p-4">
            <div className="bg-white rounded-lg shadow-md p-4 border border-gray-300">
              <div className="flex items-center justify-between mb-2">
                <h2 className="text-xl font-bold text-black">제목 및 태그 추천</h2>
                <Button variant="outline">추천받기</Button>
              </div>
              <div className="grid gap-4">
                <div>
                  <Label htmlFor="title">추천 제목</Label>
                  <Input id="title" defaultValue="제목 1" />
                </div>
                <div>
                  <Label htmlFor="tags">추천 태그</Label>
                  <Input id="tags" defaultValue="# 태그 1" />
                  <Input id="tags" defaultValue="# 태그 2" />
                  <Input id="tags" defaultValue="# 태그 3" />
                </div>
              </div>
              <div className="flex justify-end space-x-2 mt-3">
                <Button variant="outline">취소</Button>
                <Button variant="solid">저장</Button>
              </div>
            </div>
          </div>
          <div className="w-1/2 p-4">
            <div className="bg-white rounded-lg shadow-md p-4 border border-gray-300">
              <div className="flex items-center justify-between mb-2">
                <h2 className="text-xl font-bold text-black">자막 번역</h2>
                <div className="flex space-x-2">
                  <Button variant="outline">번역하기</Button>
                </div>
              </div>
              <div>
                <Label htmlFor="language">언어 선택</Label>
                <Select id="language" defaultValue="">
                  {languageOptions.map((option) => (
                    <option key={option.value} value={option.value} disabled={option.disabled}>
                      {option.label}
                    </option>
                  ))}
                </Select>
              </div>
              <div className="grid gap-4 mt-3">
                <Label htmlFor="translatedContent">번역된 자막</Label>
                <Textarea id="translatedContent" rows={5} />
              </div>
              <div className="flex justify-end space-x-2 mt-7">
                <Button variant="outline">취소</Button>
                <Button variant="solid">저장</Button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Edit;
