import React from 'react';
import SlideshowIcon from '@mui/icons-material/Slideshow';

export const Edits = () => {
  return (
    <div>

      <header className="bg-gray-900 text-white py-4 px-6">
        <div className="flex items-center justify-between">
          <div className="flex items-center">
            <div className="mr-4">
              <SlideshowIcon fontSize="large" sx={{ color: 'red' }} />
            </div>
            <div className="flex items-center">
              <h1 className="text-xl font-bold">YouTube SubHelper</h1>
              <div className="ml-6 space-x-4">
                <button className="bg-white font-medium text-black px-4 py-2 rounded">마이페이지</button>
                <button className="bg-white font-medium text-black px-4 py-2 rounded">자막편집</button>
                <button className="bg-white font-medium text-black px-4 py-2 rounded">부가기능</button>
              </div>
            </div>
          </div>
          <div className="flex items-center space-x-4">
            <button className="bg-white font-medium text-black px-4 py-2 rounded">회원정보 수정</button>
            <button className="bg-white font-medium text-black px-4 py-2 rounded">로그아웃</button>
          </div>
        </div>
      </header>


      <main className="p-6">
        <div className="bg-white p-6 rounded shadow-lg">
          <h2 className="text-lg font-bold mb-4">자막 편집</h2>


          <div className="mb-4">
            <label className="block font-medium mb-2">영상 자막</label>
            <textarea 
              className="w-full h-32 p-2 border rounded" 
              placeholder="영상 자막 내용..."
            />
          </div>


          <div className="mb-4">
            <input 
              className="w-full p-2 border rounded" 
              placeholder="단어를 수정하세요..." 
            />
            <ul className="mt-2 border rounded">
              <li className="p-2 hover:bg-gray-100">수정 단어 1 (추천: 대체 단어 1)</li>
              <li className="p-2 hover:bg-gray-100">수정 단어 2 (추천: 대체 단어 2)</li>
              <li className="p-2 hover:bg-gray-100">수정 단어 3 (추천: 대체 단어 3)</li>
            </ul>
          </div>


          <div className="flex space-x-4">
            <button className="bg-white font-medium text-black px-4 py-2 rounded">취소</button>
            <button className="bg-white font-medium text-black px-4 py-2 rounded">저장</button>
            <button className="bg-white font-medium text-black px-4 py-2 rounded">업로드</button>
          </div>
        </div>
      </main>
    </div>
  );
};

export default Edits;



