import React, { useState } from 'react';
import { Button, Label, Textarea, Command, CommandInput, CommandList, CommandEmpty, CommandGroup, CommandItem } from '../components/Components';

export const Edit = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [isFocused, setIsFocused] = useState(false);
  const [showModal, setShowModal] = useState(false); // 모달 상태 추가
  const [fileName, setFileName] = useState(""); // 파일 이름 저장

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleInputFocus = () => {
    setIsFocused(true);
  };

  const handleInputBlur = () => {
    setIsFocused(false);
  };

  // 더미 데이터
  const words = [
    { word: "단어 1", suggestion: "대체 단어 1" },
    { word: "단어 2", suggestion: "대체 단어 2" },
    { word: "단어 3", suggestion: "대체 단어 3" },
    { word: "단어 4", suggestion: "대체 단어 4" },
    { word: "단어 5", suggestion: "대체 단어 5" },
    { word: "단어 6", suggestion: "대체 단어 6" },
    { word: "단어 7", suggestion: "대체 단어 7" },
    { word: "단어 8", suggestion: "대체 단어 8" },
    { word: "단어 9", suggestion: "대체 단어 9" },
    { word: "단어 10", suggestion: "대체 단어 10" },
  ];

  const filteredWords = words.filter(
    (item) => item.word.includes(searchTerm) || item.suggestion.includes(searchTerm)
  );

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setFileName(file.name);
    }
  };

  return (
    <div className="w-full bg-white">
      <header className="bg-white text-gray-900 py-4 px-6 text-xl font-bold flex justify-between items-center">
        자막 편집
        <Button variant="outline" className="hover:bg-gray-200" onClick={() => setShowModal(true)}>
          + 자막 업로드
        </Button>
      </header>
      <div className="flex h-screen bg-white">
        <div className="w-1/2 p-4">
          <div className="bg-white rounded-lg shadow-md p-4 border border-gray-300">
            <div className="flex items-center justify-between mb-2">
              <h2 className="text-xl font-bold text-black">자막 수정</h2>
              <Button variant="outline" size="sm">점검</Button>
            </div>
            <div className="grid gap-4">
              <div>
                <Label htmlFor="content">영상 자막</Label>
                <Textarea 
                  id="content" 
                  rows={5} 
                  defaultValue={`0:11 오늘도 아침엔 입에 빵을 물고

0:15 똑같이 하루를 시작하고

0:20 온종일 한 손엔 아이스 아메리카노

0:25 피곤해 죽겠네

0:28 지하철 속 이 장면 어제 꿈에서 봤나

0:32 아참 매일이지 지나치고

0:36 바쁜 이 삶에 그냥 흔한 날에

0:41 그 애를 보고 말야

0:44 평온했던 하늘이 무너지고

0:49 어둡던 눈앞이 붉어지며

0:53 뭔가 잊고 온 게 있는 것 같아

0:57 괜히 이상하게 막 울 것만 같고

1:02 그냥 지나치는 게 나을 것 같아

1:05 나는 생각은 딱 질색이니까`}
                />
              </div>
              <div className="relative">
                <Command>
                  <CommandInput 
                    placeholder="단어를 수정하세요..." 
                    onChange={handleInputChange} 
                    onFocus={handleInputFocus} 
                    onBlur={handleInputBlur}
                  />
                  {isFocused && (
                    <CommandList>
                      {filteredWords.length === 0 ? (
                        <CommandEmpty>No results found.</CommandEmpty>
                      ) : (
                        <CommandGroup>
                          {filteredWords.map((item, index) => (
                            <CommandItem key={index}>
                              <Button variant="solid" size="sm" className="mr-2">수정</Button>
                              <span>{item.word}</span>
                              <span className="ml-2 text-gray-500">(추천: {item.suggestion})</span>
                            </CommandItem>
                          ))}
                        </CommandGroup>
                      )}
                    </CommandList>
                  )}
                </Command>
              </div>
              <div className="mt-7" />
              <div className="flex justify-end space-x-2">
                <Button variant="outline" size="sm">취소</Button>
                <Button variant="solid" size="sm">저장</Button>
                <Button variant="solid" size="sm">업로드</Button>
              </div>
            </div>
          </div>
        </div>
        <div className="w-1/2 p-4">
          <div className="bg-white rounded-lg shadow-md p-4 border border-gray-300">
            <div className="relative w-full h-0 pb-[56.25%]">
              <iframe
                src="https://www.youtube.com/embed/ATK7gAaZTOM?"
                className="absolute top-0 left-0 w-full h-full"
                frameBorder="0"
                allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
              />
            </div>
          </div>
        </div>
      </div>

      {/* 자막 업로드 모달 */}
{showModal && (
  <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
    <div className="bg-white p-6 rounded shadow-lg">
      <h2 className="text-xl font-bold mb-4">유튜브 주소 입력</h2>
      <input 
        type="text" 
        placeholder="유튜브 영상 URL을 입력하세요" 
        value={fileName} 
        onChange={(e) => setFileName(e.target.value)} 
        className="border p-2 w-full mb-4"
      />
      {fileName && <p className="text-gray-500 mb-4">입력된 주소: {fileName}</p>}
      <div className="flex justify-end space-x-2">
        <Button 
          variant="outline" 
          size="sm" 
          className="hover:bg-gray-200" 
          onClick={() => setShowModal(false)}
        >
          취소
        </Button>
        <Button 
          variant="solid" 
          size="sm" 
          className="hover:bg-blue-600"
          onClick={() => {
            // 유튜브 URL 처리 로직
            setShowModal(false); // 모달 닫기
          }}
        >
          업로드
        </Button>
      </div>
    </div>
  </div>
)}
    </div>
  );
};

export default Edit;
