import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button } from '../components/Components';
import axios from 'axios';

const baseAddress = "http://localhost:3000";

export const Mypage = () => {
  const navigate = useNavigate();
  const [subtitles, setSubtitles] = useState([]); // 자막 목록 저장
  const [showModal, setShowModal] = useState(false); // 모달창 상태
  const [title, setProjectName] = useState(''); // 제목
  const [url, setProjectUrl] = useState(''); // URL
  const [userData, setUserData] = useState({ name: '', email: '' }); // 사용자 데이터

  // 자막 목록 로드
  useEffect(() => {
    const storedSubtitles = localStorage.getItem('subtitles');
    if (storedSubtitles) {
      setSubtitles(JSON.parse(storedSubtitles));
    }
  }, []);

  // 사용자 데이터 로드
  useEffect(() => {
    const fetchUserData = async () => {
      const token = localStorage.getItem('token');
      try {
        const response = await axios.get(`${baseAddress}/user/value`, {
          headers: { 'Authorization': `Bearer ${token}` }
        });

        // API의 응답 데이터가 배열이 아니라 객체일 경우를 대비하여 구조 확인
        if (response.data && Array.isArray(response.data)) {
          setUserData({
            name: response.data[0]?.name || '이름 없음',
            email: response.data[1]?.email || '이메일 없음',
          });
        } else {
          setUserData({
            name: response.data?.name || '이름 없음',
            email: response.data?.email || '이메일 없음',
          });
        }
      } catch (error) {
        console.error('사용자 데이터 로드 중 오류 발생:', error);
      }
    };

    fetchUserData();
  }, []);

  // 프로젝트 제목을 가져오는 함수
  const fetchProjectTitle = async () => {
    const token = localStorage.getItem('token');
    try {
      const response = await axios.get(`${baseAddress}/project/title`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      // 프로젝트 제목이 배열로 반환될 경우
      if (Array.isArray(response.data)) {
        return response.data;
      } else {
        return []; // 배열이 아닐 경우 빈 배열 반환
      }
    } catch (error) {
      console.error('프로젝트 제목 로드 중 오류 발생:', error);
      return [];
    }
  };

  // 자막 추가
  const handleAddSubtitle = async () => {
    const token = localStorage.getItem('token');
    const projectTitles = await fetchProjectTitle();

    const newSub = {
      title: title,
      summary: projectTitles.length > 0 ? projectTitles[0].summary : '요약 없음', // 프로젝트 제목이 있으면 첫 번째 요약을 사용
      date: new Date().toLocaleDateString('ko-KR', { year: 'numeric', month: 'long', day: 'numeric' }),
    };

    try {
      const response = await axios.post(`${baseAddress}/project`, {
        project_title: title,
        project_url: url,
      }, {
        headers: { 'Authorization': `Bearer ${token}` }
      });

      console.log('서버 응답:', response.data);

      // 자막 목록 업데이트
      const updatedSubtitles = [...subtitles, newSub];
      setSubtitles(updatedSubtitles);
      localStorage.setItem('subtitles', JSON.stringify(updatedSubtitles)); // 로컬 스토리지에 저장

      // 입력 필드 초기화 및 모달 닫기
      setShowModal(false);
      setProjectName('');
      setProjectUrl('');
    } catch (error) {
      const errorMessage = error.response?.data?.message || '오류 발생.';
      console.error('오류 발생:', errorMessage);
      alert(errorMessage);
    }
  };

  const handleEditClick = () => {
    navigate('/Edit');
  };

  return (
    <div className="w-full bg-white text-gray-900 min-h-screen">
      <div className="flex flex-col h-screen">
        <header className="bg-white text-gray-900 py-4 px-6 flex justify-between items-center">
          <h2 className="text-xl font-bold">마이페이지</h2>
          <div className="flex space-x-2">
            <Button
              variant="outline"
              className="hover:bg-gray-200"
              onClick={() => setShowModal(true)}
            >
              + 자막 추가
            </Button>
          </div>
        </header>

        <div className="bg-white rounded-lg p-4 border border-gray-300">
          <h2 className="text-xl font-bold">내 정보</h2>
          <p><span className="font-bold">이름: {userData.name}</span></p>
          <p><span className="font-bold">이메일: {userData.email}</span></p>
        </div>

        <div className="flex-1 overflow-auto p-4">
          <div className="grid gap-4">
            {subtitles.map((subtitle, index) => (
              !subtitle.deleted && ( // 삭제된 자막은 렌더링하지 않음
                <div key={index} className="bg-white rounded-lg shadow-md p-4 border border-gray-300">
                  <h2 className="text-xl font-bold mb-2">{subtitle.title}</h2>
                  <div className="grid gap-2">
                    <div className="flex items-center justify-between">
                      <div>
                        <h3 className="font-medium">{subtitle.summary}</h3>
                        <p className="text-sm text-gray-500">{subtitle.date}</p>
                      </div>
                      <div className="flex items-center space-x-2">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={handleEditClick}
                        >
                          자막 편집
                        </Button>
                        <Button
                          variant="solid"
                          size="sm"
                          onClick={() => handleDeleteSubtitle(index)}
                        >
                          삭제
                        </Button>
                      </div>
                    </div>
                  </div>
                </div>
              )
            ))}
          </div>
        </div>
      </div>

      {showModal && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded shadow-lg">
            <h2 className="text-xl font-bold mb-4">영상 선택</h2>
            <input
              type="text"
              placeholder="영상 제목을 입력하세요.."
              value={title}
              onChange={(e) => setProjectName(e.target.value)}
              className="border bg-white p-2 w-full mb-4"
            />
            <input
              type="text"
              placeholder="영상 URL 입력하세요.."
              value={url}
              onChange={(e) => setProjectUrl(e.target.value)}
              className="border bg-white p-2 w-full mb-4"
            />
            <div className="flex justify-end space-x-2">
              <Button
                variant="outline"
                size="sm"
                className="hover:bg-gray-200"
                onClick={() => setShowModal(false)}
              >
                취소
              </Button>
              <Button
                variant="solid"
                size="sm"
                className="hover:bg-blue-600"
                onClick={handleAddSubtitle}
              >
                추가
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Mypage;
