import React, { useState } from 'react';
import SlideshowIcon from '@mui/icons-material/Slideshow';

export const Mypage = () => {

  const [subtitles, setSubtitles] = useState([
    { title: '수정된 자막 1', summary: '요약된 내용 1', date: '2024년 7월 1일' },
    { title: '수정된 자막 2', summary: '요약된 내용 2', date: '2024년 7월 2일' },
    { title: '수정된 자막 3', summary: '요약된 내용 3', date: '2024년 7월 3일' },
  ]);


  const [showModal, setShowModal] = useState(false);
  const [newSubtitle, setNewSubtitle] = useState({ title: '', summary: '' });


  const handleAddSubtitle = () => {
    const newSub = {
      title: `수정된 자막 ${subtitles.length + 1}`,
      summary: newSubtitle.summary,
      date: new Date().toLocaleDateString('ko-KR', { year: 'numeric', month: 'long', day: 'numeric' }),
    };
    setSubtitles([...subtitles, newSub]);
    setShowModal(false);
    setNewSubtitle({ title: '', summary: '' });
  };


  const handleDeleteSubtitle = (index) => {
    const updatedSubtitles = subtitles.filter((_, i) => i !== index);
    setSubtitles(updatedSubtitles);
  };

  return (
    <div>
      {/* Main Content */}
      <main className="p-6">
        {/* Page Title */}
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-2xl font-bold">마이페이지</h2>
          <button
            className="text-blue-600 text-lg font-semibold"
            onClick={() => setShowModal(true)}
          >
            + 자막 추가
          </button>
        </div>


        <div className="space-y-4">
          {subtitles.map((subtitle, index) => (
            <div key={index} className="bg-white p-4 rounded shadow">
              <h3 className="text-lg font-bold">{subtitle.title}</h3>
              <p className="text-gray-600">{subtitle.summary}</p>
              <p className="text-gray-400">{subtitle.date}</p>
              <div className="flex space-x-2 mt-2">
                <button className="bg-gray-200 text-black px-3 py-1 rounded">자막 편집</button>
                <button className="bg-gray-200 text-black px-3 py-1 rounded">부가기능</button>
                <button
                  className="bg-gray-200 text-black px-3 py-1 rounded"
                  onClick={() => handleDeleteSubtitle(index)}
                >
                  삭제
                </button>
              </div>
            </div>
          ))}
        </div>
      </main>


      {showModal && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded shadow-lg">
            <h2 className="text-xl font-bold mb-4">자막 추가</h2>
            <input
              type="text"
              placeholder="자막 내용을 입력하세요"
              value={newSubtitle.summary}
              onChange={(e) => setNewSubtitle({ ...newSubtitle, summary: e.target.value })}
              className="border p-2 w-full mb-4"
            />
            <div className="flex justify-end space-x-2">
              <button
                className="bg-gray-200 text-black px-4 py-2 rounded"
                onClick={() => setShowModal(false)}
              >
                취소
              </button>
              <button
                className="bg-blue-600 text-white px-4 py-2 rounded"
                onClick={handleAddSubtitle}
              >
                추가
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Mypage;
