import React, { useState } from 'react';
import { Button, Label, Textarea, Command, CommandInput, CommandList, CommandEmpty, CommandGroup, CommandItem, Input, Select, Audio } from '../components/Components';
import axios from 'axios'; // axios 임포트 추가

export const Edit = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [isFocused, setIsFocused] = useState(false);
  const [showModal, setShowModal] = useState(false); // 모달 상태 추가
  const [fileName, setFileName] = useState(""); // 파일 이름 저장
  const [name, setProjectName] = useState(""); // 영상 제목 저장
  const [url, setProjectUrl] = useState("");   // 영상 링크 저장
  const [subtitleContent, setSubtitleContent] = useState(""); // 자막 내용을 저장할 상태 추가

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleInputFocus = () => {
    setIsFocused(true);
  };

  const handleInputBlur = () => {
    setIsFocused(false);
  };

  // 더미 데이터
  const words = [
    { word: "단어 1", suggestion: "대체 단어 1" },
    { word: "단어 2", suggestion: "대체 단어 2" },
    { word: "단어 3", suggestion: "대체 단어 3" },
    { word: "단어 4", suggestion: "대체 단어 4" },
    { word: "단어 5", suggestion: "대체 단어 5" },
    { word: "단어 6", suggestion: "대체 단어 6" },
    { word: "단어 7", suggestion: "대체 단어 7" },
    { word: "단어 8", suggestion: "대체 단어 8" },
    { word: "단어 9", suggestion: "대체 단어 9" },
    { word: "단어 10", suggestion: "대체 단어 10" },
  ];

  const filteredWords = words.filter(
    (item) => item.word.includes(searchTerm) || item.suggestion.includes(searchTerm)
  );

  const languageOptions = [
    { value: "", label: "언어 선택", disabled: true },
    { value: "en", label: "영어" },
    { value: "es", label: "스페인어" },
    { value: "fr", label: "프랑스어" },
    { value: "de", label: "독일어" },
    { value: "ja", label: "일본어" },
    { value: "zh", label: "중국어" },
  ];

  const handleDownload = async () => {
    try {
      // 파일 다운로드 요청
      const response = await axios.get('http://localhost:3000/api/download', {
        responseType: 'blob', // 파일 다운로드를 위해 blob 형식으로 응답 받기
      });

      // Blob 객체를 URL로 변환
      const url = window.URL.createObjectURL(new Blob([response.data]));

      // 다운로드 링크 생성
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'subtitle_file.txt'); // 원하는 파일 이름 설정

      // 링크 클릭하여 다운로드
      document.body.appendChild(link);
      link.click();

      // 링크 제거
      link.parentNode.removeChild(link);

      // 다운로드된 파일을 읽기
      const fileContent = await response.data.text(); // Blob 데이터에서 텍스트 읽기
      setSubtitleContent(fileContent); // 자막 내용 상태 업데이트

    } catch (error) {
      console.error('파일 다운로드 중 오류 발생:', error);
    }
  };

  return (
    <div className="w-full bg-white">
      {/* 상단 페이지: 자막 편집 */}
      <div className="bg-white">
        <header className="bg-white text-gray-900 py-4 px-6 text-xl font-bold flex justify-between items-center">
          자막 편집
          <Button
            variant="outline"
            className="hover:bg-gray-200"
            onClick={() => setShowModal(true)} // 버튼 클릭 시 모달 열림
          >
            + 자막 추가
          </Button>
        </header>
        <div className="flex bg-white">
          <div className="w-1/2 p-4">
            <div className="bg-white rounded-lg shadow-md p-4 border border-gray-300">
              <div className="flex items-center justify-between mb-2">
                <h2 className="text-xl font-bold text-black">자막 수정</h2>
                <div className="flex space-x-2">
                  <Button variant="outline" size="sm">생성</Button>
                  <Button variant="solid" size="sm">점검</Button>
                </div>
              </div>
              <div className="grid gap-4">
                <div>
                  <Label htmlFor="content">영상 자막</Label>
                  <Textarea
                    id="content"
                    rows={5}
                    value={subtitleContent} // 상태에 저장된 자막 내용을 표시
                    onChange={(e) => setSubtitleContent(e.target.value)} // 변경 시 상태 업데이트
                  />
                </div>
                <div className="relative">
                  <Command>
                    <CommandInput
                      placeholder="단어를 수정하세요..."
                      onChange={handleInputChange}
                      onFocus={handleInputFocus}
                      onBlur={handleInputBlur}
                    />
                    {isFocused && (
                      <CommandList>
                        {filteredWords.length === 0 ? (
                          <CommandEmpty>No results found.</CommandEmpty>
                        ) : (
                          <CommandGroup>
                            {filteredWords.map((item, index) => (
                              <CommandItem key={index}>
                                <Button variant="solid" size="sm" className="mr-2">수정</Button>
                                <span>{item.word}</span>
                                <span className="ml-2 text-gray-500">(추천: {item.suggestion})</span>
                              </CommandItem>
                            ))}
                          </CommandGroup>
                        )}
                      </CommandList>
                    )}
                  </Command>
                </div>
                <div className="mt-7" />
                <div className="flex justify-end space-x-2">
                  <Button variant="outline" size="sm">취소</Button>
                  <Button
                    variant="solid"
                    size="sm"
                    onClick={handleDownload} // 다운로드 버튼 클릭 시 파일 다운로드 함수 호출
                  >
                    다운로드
                  </Button>
                  <Button variant="solid" size="sm">저장</Button>
                  <Button variant="solid" size="sm">업로드</Button>
                </div>
              </div>
            </div>
          </div>
          <div className="w-1/2 p-4">
            <div className="bg-white rounded-lg shadow-md p-4 border border-gray-300">
              <div className="relative w-full h-0 pb-[56.25%]">
                <iframe
                  src="https://www.youtube.com/embed/ATK7gAaZTOM?"
                  className="absolute top-0 left-0 w-full h-full"
                  frameBorder="0"
                  allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                  allowFullScreen
                />
              </div>
            </div>
          </div>
        </div>

        {showModal && (
          <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
            <div className="bg-white p-6 rounded shadow-lg">
              <h2 className="text-xl font-bold mb-4">영상 선택</h2>
              <input
                type="text"
                placeholder="영상 제목을 입력하세요.."
                value={name} // project_name 상태에 바인딩
                onChange={(e) => setProjectName(e.target.value)} // 입력값 변경 시 상태 업데이트
                className="border bg-white p-2 w-full mb-4"
              />
              <input
                type="text"
                placeholder="영상 URL을 입력하세요.."
                value={url} // project_url 상태에 바인딩
                onChange={(e) => setProjectUrl(e.target.value)} // 입력값 변경 시 상태 업데이트
                className="border bg-white p-2 w-full mb-4"
              />
              <div className="flex justify-end space-x-2">
                <Button
                  variant="outline"
                  size="sm"
                  className="hover:bg-gray-200"
                  onClick={() => setShowModal(false)} // 취소 버튼 클릭 시 모달 닫기
                >
                  취소
                </Button>
                <Button
                  variant="solid"
                  size="sm"
                  className="hover:bg-blue-600"
                  onClick={async () => {
                    try {
                      // 서버에 자막 추가 요청
                      const response = await axios.post('/api/subtitles', {
                        title: name,
                        url: url,
                        // 필요한 다른 데이터도 포함할 수 있음
                      });
                      console.log(response.data); // 서버 응답 로그
                      setShowModal(false); // 요청 성공 후 모달 닫기
                    } catch (error) {
                      console.error('자막 추가 중 오류 발생:', error);
                    }
                  }}
                >
                  저장
                </Button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Edit;
