import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button } from '../components/Components';
import axios from 'axios';

const baseAddress = "http://localhost:3000";

export const Mypage = () => {
  const navigate = useNavigate(); // 페이지 이동을 위한 useNavigate 훅
  const [subtitles, setSubtitles] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [title, setProjectName] = useState(''); // "영상 제목을 입력하세요" 입력값 저장
  const [url, setProjectUrl] = useState('');   // "영상 링크를 입력하세요" 입력값 저장
  const [userData, setUserData] = useState({ name: '', email: '' }); // 유저 데이터 상태
  const [projectTitles, setProjectTitles] = useState([]); // 프로젝트 제목 상태 추가

  // 유저 데이터와 자막 제목을 가져오는 useEffect
  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem('token'); // 로컬스토리지에서 토큰 가져오기

      try {
        // 유저 데이터 가져오기
        const userResponse = await axios.get(`${baseAddress}/user/value`, {
          headers: {
            'Authorization': `Bearer ${token}`, // 인증 토큰을 헤더에 포함
          }
        });

        setUserData({
          name: userResponse.data[0].name,
          email: userResponse.data[0].email,
        });

        // 자막 제목 가져오기
        const titlesResponse = await axios.get(`${baseAddress}/project/title`, {
          headers: {
            'Authorization': `Bearer ${token}`,
          }
        });
        setProjectTitles(titlesResponse.data); // 서버에서 받은 프로젝트 제목으로 상태 업데이트

        // 자막 데이터 가져오기
        const subtitlesResponse = await axios.get(`${baseAddress}/subtitles`, {
          headers: {
            'Authorization': `Bearer ${token}`, // 인증 토큰을 헤더에 포함
          }
        });
        setSubtitles(subtitlesResponse.data); // 서버에서 받은 자막 데이터로 상태 업데이트
      } catch (error) {
        console.error('데이터를 가져오는 중 에러 발생:', error);
      }
    };

    fetchData(); // API 호출 함수 실행
  }, []);

  const handleAddSubtitle = async () => {
    const token = localStorage.getItem('token');

    const newSub = {
      title: title,
      date: new Date().toLocaleDateString('ko-KR', { year: 'numeric', month: 'long', day: 'numeric' }),
    };

    // 서버에 자막 데이터 전송
    try {
      const response = await axios.post(`${baseAddress}/project`, {
        project_title: title, // 서버로 전송하는 프로젝트 제목은 입력된 값
        project_url: url,
      }, {
        headers: {
          'Authorization': `Bearer ${token}`, // 토큰을 헤더에 포함
        }
      });

      console.log('서버 응답:', response.data);

      // 자막 추가 후 상태 업데이트
      setSubtitles([...subtitles, newSub]); // 새로운 자막 추가
      setShowModal(false);
      setProjectName(''); // 상태 초기화
      setProjectUrl('');  // 상태 초기화
    } catch (error) {
      const errorMessage = error.response?.data?.message || '서버 오류가 발생했습니다.';
      console.error('에러 발생:', errorMessage);
      alert(errorMessage);
    }
  };

  const handleDeleteSubtitle = (index) => {
    const updatedSubtitles = subtitles.filter((_, i) => i !== index);
    setSubtitles(updatedSubtitles);
  };

  const handleEditClick = () => {
    navigate('/Edit'); // 자막 편집 페이지로 이동
  };

  return (
    <div className="w-full bg-white text-gray-900 min-h-screen">
      <div className="flex flex-col h-screen">
        <header className="bg-white text-gray-900 py-4 px-6 flex justify-between items-center">
          <h2 className="text-xl font-bold">마이페이지</h2>
          <div className="flex space-x-2">
            <Button 
              variant="outline" 
              className="hover:bg-gray-200" 
              onClick={() => setShowModal(true)}
            >
              + 자막 추가
            </Button>
          </div>
        </header>

        {/* 유저 정보 섹션 */}
        <div className="bg-white rounded-lg p-4 border border-gray-300">
          <h2 className="text-xl font-bold">내 정보</h2>
          <p><span className="font-bold">이름: {userData.name}</span></p>
          <p><span className="font-bold">이메일: {userData.email}</span></p>
        </div>

        {/* 자막 제목 리스트 섹션 */}
        <div className="bg-white rounded-lg p-4 border border-gray-300 mt-4">
          <h2 className="text-xl font-bold">자막 제목 리스트</h2>
          <ul className="list-disc pl-6">
            {projectTitles.map((title, index) => (
              <li key={index} className="text-gray-700">{title}</li>
            ))}
          </ul>
        </div>

        {/* 자막 리스트 섹션 */}
        <div className="flex-1 overflow-auto p-4">
          <div className="grid gap-4">
            {subtitles.map((subtitle, index) => (
              <div key={index} className="bg-white rounded-lg shadow-md p-4 border border-gray-300">
                <h2 className="text-xl font-bold mb-2">{subtitle.title}</h2>
                <div className="grid gap-2">
                  <div className="flex items-center justify-between">
                    <div>
                      <h3 className="font-medium">{subtitle.summary}</h3>
                      <p className="text-sm text-gray-500">{subtitle.date}</p>
                    </div>
                    <div className="flex items-center space-x-2">
                      <Button 
                        variant="outline" 
                        size="sm" 
                        onClick={handleEditClick} // 자막 편집 버튼 클릭 시 페이지 이동
                      >
                        자막 편집
                      </Button>
                      <Button 
                        variant="solid" 
                        size="sm" 
                        onClick={() => handleDeleteSubtitle(index)}
                      >
                        삭제
                      </Button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {showModal && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded shadow-lg">
            <h2 className="text-xl font-bold mb-4">영상 선택</h2>
            <input
              type="text"
              placeholder="영상 제목을 입력하세요.."
              value={title}
              onChange={(e) => setProjectName(e.target.value)}
              className="border bg-white p-2 w-full mb-4"
            />
            <input
              type="text"
              placeholder="영상 URL 입력하세요.."
              value={url}
              onChange={(e) => setProjectUrl(e.target.value)}
              className="border bg-white p-2 w-full mb-4"
            />
            <div className="flex justify-end space-x-2">
              <Button
                variant="outline"
                size="sm"
                className="hover:bg-gray-200"
                onClick={() => setShowModal(false)}
              >
                취소
              </Button>
              <Button
                variant="solid"
                size="sm"
                className="hover:bg-blue-600"
                onClick={handleAddSubtitle}
              >
                추가
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Mypage;
