import React, { useState } from 'react';
import { Button, Label, Textarea, Command, CommandInput, CommandList, CommandEmpty, CommandGroup, CommandItem, Input, Select, Audio } from '../components/Components';

const SubtitleEditor = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [isFocused, setIsFocused] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [fileName, setFileName] = useState("");
  const [subtitles, setSubtitles] = useState('');

  const languageOptions = [
    { value: "", label: "언어 선택", disabled: true },
    { value: "en", label: "영어" },
    { value: "es", label: "스페인어" },
    { value: "fr", label: "프랑스어" },
    { value: "de", label: "독일어" },
    { value: "ja", label: "일본어" },
    { value: "zh", label: "중국어" },
  ];

  const words = [
    { word: "단어 1", suggestion: "대체 단어 1" },
    { word: "단어 2", suggestion: "대체 단어 2" },
    { word: "단어 3", suggestion: "대체 단어 3" },
  ];

  const filteredWords = words.filter(
    (item) => item.word.includes(searchTerm) || item.suggestion.includes(searchTerm)
  );

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleInputFocus = () => {
    setIsFocused(true);
  };

  const handleInputBlur = () => {
    setIsFocused(false);
  };

  return (
    <div className="w-full bg-white">
      <header className="bg-white text-gray-900 py-4 px-6 text-xl font-bold flex justify-between items-center">
        자막 편집 및 도구
        <Button
          variant="outline"
          className="hover:bg-gray-200"
          onClick={() => {
            setFileName("");
            setShowModal(true);
          }}
        >
          + 자막 업로드
        </Button>
      </header>

      <div className="flex flex-col h-screen bg-white">
        <div className="flex flex-1">
          {/* 상단 좌측: 자막 수정 */}
          <div className="w-1/2 p-4">
            <div className="bg-white rounded-lg shadow-md p-4 border border-gray-300">
              <h2 className="text-xl font-bold text-black">자막 수정</h2>
              <Button variant="outline" size="sm">점검</Button>
              <Textarea 
                rows={5} 
                value={subtitles}
                onChange={(e) => setSubtitles(e.target.value)}
                placeholder="여기에 자막을 입력하세요..."
              />
              <div className="relative mt-4">
                <Command>
                  <CommandInput 
                    placeholder="단어를 수정하세요..." 
                    onChange={handleInputChange} 
                    onFocus={handleInputFocus} 
                    onBlur={handleInputBlur}
                  />
                  {isFocused && (
                    <CommandList>
                      {filteredWords.length === 0 ? (
                        <CommandEmpty>No results found.</CommandEmpty>
                      ) : (
                        <CommandGroup>
                          {filteredWords.map((item, index) => (
                            <CommandItem key={index}>
                              <Button variant="solid" size="sm" className="mr-2">수정</Button>
                              <span>{item.word}</span>
                              <span className="ml-2 text-gray-500">(추천: {item.suggestion})</span>
                            </CommandItem>
                          ))}
                        </CommandGroup>
                      )}
                    </CommandList>
                  )}
                </Command>
              </div>
              <div className="flex justify-end space-x-2 mt-4">
                <Button variant="outline">취소</Button>
                <Button variant="solid">저장</Button>
                <Button variant="solid">업로드</Button>
                <Button variant="solid">갱신</Button> {/* 갱신 버튼 추가 */}
              </div>
            </div>
          </div>

          {/* 상단 우측: 동영상 */}
          <div className="w-1/2 p-4">
            <div className="bg-white rounded-lg shadow-md p-4 border border-gray-300">
              <div className="relative w-full h-0 pb-[56.25%]">
                <iframe
                  src="https://www.youtube.com/embed/ATK7gAaZTOM?"
                  className="absolute top-0 left-0 w-full h-full"
                  frameBorder="0"
                  allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                  allowFullScreen
                />
              </div>
            </div>
          </div>
        </div>

        <div className="flex flex-1 bg-white">
          {/* 하단 좌측: 제목 및 태그 추천 */}
          <div className="w-1/2 p-4">
            <div className="bg-white rounded-lg shadow-md p-4 border border-gray-300">
              <h2 className="text-xl font-bold">제목 및 태그 추천</h2>
              <div className="grid gap-4">
                <div>
                  <Label htmlFor="title">추천 제목</Label>
                  <Input id="title" defaultValue="제목 1" />
                </div>
                <div>
                  <Label htmlFor="tags">추천 태그</Label>
                  <Input id="tags" defaultValue="# 태그 1" />
                  <Input id="tags" defaultValue="# 태그 2" />
                  <Input id="tags" defaultValue="# 태그 3" />
                </div>
                <div className="flex justify-end space-x-2">
                  <Button variant="outline">취소</Button>
                  <Button variant="solid">저장</Button>
                  <Button variant="solid">업로드</Button>
                  <Button variant="solid">추천받기</Button> {/* 추천받기 버튼 추가 */}
                </div>
              </div>
            </div>
          </div>

          {/* 하단 우측: 자막 번역 및 AI 보이스 */}
          <div className="w-1/2 p-4">
            <div className="bg-white rounded-lg shadow-md p-4 border border-gray-300">
              <h2 className="text-xl font-bold">자막 번역</h2>
              <div className="flex items-center mb-2">
                <Select id="translation-language" options={languageOptions} className="mr-2" />
                <Button variant="outline">번역</Button>
              </div>
              <div>
                <Label htmlFor="translation">자막 번역</Label>
                <Textarea
                  id="translation"
                  rows={5}
                  defaultValue={`0:11 This morning, I ate bread in my mouth

0:15 Start your day exactly the same

0:20 Iced americano in one hand all day long`}
                />
              </div>
              <div>
                <Label htmlFor="voice">AI 보이스</Label>
                <div className="flex items-center justify-between">
                  <Audio src="generated-voice.mp3" />
                  <Button variant="outline">생성</Button>
                </div>
              </div>
              <div className="flex justify-end space-x-2 mt-4">
                <Button variant="outline">취소</Button>
                <Button variant="solid">저장</Button>
                <Button variant="solid">업로드</Button>
              </div>
            </div>
          </div>
        </div>
      </div>


      {/* 자막 업로드 모달 */}
      {showModal && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded shadow-lg w-[500px]">
            <h2 className="text-xl font-bold mb-4">유튜브 주소 입력</h2>
            <input 
              type="text" 
              placeholder="유튜브 영상 URL을 입력하세요" 
              value={fileName} 
              onChange={(e) => setFileName(e.target.value)} 
              className="border p-2 w-full mb-4"
            />
            <div className="flex justify-end space-x-2">
              <Button 
                variant="outline" 
                size="sm" 
                className="hover:bg-gray-200" 
                onClick={() => setShowModal(false)}
              >
                취소
              </Button>
              <Button 
                variant="solid" 
                size="sm" 
                className="hover:bg-blue-600"
                onClick={() => {
                  // 유튜브 URL 처리 로직
                  setShowModal(false); // 모달 닫기
                }}
              >
                업로드
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SubtitleEditor;
